package july10.area;

/*
 * Write a program to calculate Area of a Rectangle
 */

public class Rectangle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int length = 4;
		int breadth = 6;
		
		// Formula: Area of a Rectangle = length * breadth
		int area = length * breadth;
		
		System.out.println("Area of a rectangle with length = " + length + " and breadth = " + breadth + " is " + area + " sq. units");
	}

}
