package july10.area;

/*
 * Write a program to calculate Area of a Square
 */

public class Square {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int side = 4;
		
		// Formula: Area of a Square = side * side
		int area = side * side;
		
		System.out.println("Area of a square with side = " + side + " is " + area + " sq. units");
	}

}
